import React, { useState, useEffect } from 'react'
import AllNames from './components/AllNames'
import Filter from './components/Filter'
import NewPerson from './components/NewPerson'
import axios from 'axios'

const App = () => {
  const [persons, setPersons] = useState([]) 

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')

  useEffect(() => {
    console.log('effect')
    axios
      .get('http://localhost:3001/persons')
      .then(response => {
        console.log('promise fulfilled')
        setPersons(response.data)
      })
  }, [])

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }
  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }
  const addName = (event) => {
    event.preventDefault()
    const name = {
      name: newName,
      number: newNumber,
    }
  for(let i=0;i<persons.length;i++){
    if(persons[i].name === newName){
      alert(`${newName} already added to the phonebook`);
      return;
    }
     if(persons[i].number === newNumber){
      alert(`${newNumber} already added to the phonebook`);
      return;
    }
  }

    console.log('button clicked', event.target)

    setPersons(persons.concat(name));
    setNewName('');
    setNewNumber('');

  }
  const handleSearch = (event) => {
    const result = persons.filter(function(person) {
     return person.name.includes(event.target.value) ||
            person.number.includes(event.target.value);

    })

    setPersons(result);

  }



  return (
    <div>
      <h2>Phonebook</h2>
      <Filter handleSearch={handleSearch}/>
      <h2>Add person</h2>
      <NewPerson newName={newName} newNumber={newNumber} handleNameChange={handleNameChange} handleNumberChange={handleNumberChange} addName={addName} />
      <h2>Numbers</h2>
      <AllNames persons={persons}/>
    </div>
  )

}

export default App
