import React, { useState } from 'react'
import AllNames from './components/AllNames'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456' },
    { name: 'Ada Lovelace', number: '39-44-5323523' },
    { name: 'Dan Abramov', number: '12-43-234345' },
    { name: 'Mary Poppendieck', number: '39-23-6423122' }
  ]) 

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const[result, setResult] = useState([])

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }
  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }
  const addName = (event) => {
    event.preventDefault()
    const name = {
      name: newName,
      number: newNumber,
    }
  for(let i=0;i<persons.length;i++){
    if(persons[i].name === newName){
      alert(`${newName} already added to the phonebook`);
      return;
    }
     if(persons[i].number === newNumber){
      alert(`${newNumber} already added to the phonebook`);
      return;
    }
  }

    console.log('button clicked', event.target)

    setPersons(persons.concat(name));
    setNewName('');
    setNewNumber('');

  }
  const handleSearch = (event) => {
    const result = persons.filter(function(person) {
     return person.name.includes(event.target.value) ||
            person.number.includes(event.target.value);

    })

    setResult(result)

  }



  return (
    <div>
      <h2>Phonebook</h2>
      <div>
        Search: <input  onChange={handleSearch} />
        </div>
      <h2>Add person</h2>
      <form onSubmit={addName}>
        <div>
          name: <input value={newName}
          onChange={handleNameChange} />
        </div>
        <div>
          number: <input value={newNumber}
          onChange={handleNumberChange} />
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <AllNames persons={result}/>
    </div>
  )

}

export default App
